mapper.update:
UPDATE SHOPPER
SET SHOPPER_AUTO_LOGIN = #{shopperAutoLogin}
WHERE SHOPPER_NO=#{shopperNo}

mapper.select:
SELECT SHOPPER_ID as shopperId
FROM SHOPPER
WHERE SHOPPER_ID= #{shopperId}

mapper.update:
UPDATE SHOPPER
SET SHOPPER_ID = #{shopperId}
WHERE SHOPPER_NO = #{shopperNo}

